con:configuration.scriptText:
// to fetch the customerid and store it in custom properties.
import com.eviware.soapui.support.XmlHolder
def resp= context.testCase.testSteps['Create Customer'].getPropertyValue('Response')

log.info resp
def holder = new XmlHolder(resp)
def customerId= holder.getNodeValue("//*:id")
context.testCase.setPropertyValue("customerId",customerId)

con:config.script:
//if address is present in create customer goto search by addressID else restireveCustomer details by customerID
def postcode = context.expand('${Customer Creation Properties#postcode}')
log.info "$postcode"

if(postcode!="")
{
    log.info "Address is available for customer"

    testRunner.gotoStepbyName("Create Customer with Address")

}
else
{
    log.info "Address is not available for customer"
    testRunner.gotoStepByName("Create Customer without Address")
}

con:configuration.scriptText:
// to fetch the customerid and store it in custom properties.
import com.eviware.soapui.support.XmlHolder
def respo= context.testCase.testSteps['Create Customer with Address'].getPropertyValue('Response')

log.info respo
def holder = new XmlHolder(respo)
def customerId= holder.getNodeValue("//*:id")
context.testCase.setPropertyValue("customerId",customerId)

def holder1 = new XmlHolder(respo)
def addressId=holder1.getNodeValue("//result/addresses/item/id")
context.testCase.setPropertyValue("addressId",addressId)

con:config.script:
//if address is present in create customer goto search by addressID else restireveCustomer details by customerID
def defaultBilling = context.expand('${Create Customer with Address#Response#//*:defaultBilling}')

log.info "$defaultBilling"
def addressId =testRunner.testCase.getPropertyValue("addressId")
log.info "$addressId"
if("$defaultBilling"!="$addressId")
{
    log.info "Address is not available for customer"

    testRunner.gotoStepByName("Retrieve by CustomerId")
}
else
{
    log.info "addressId is $defaultBilling"
    testRunner.gotoStepByName("Retrieve by AddressId")
}

con:configuration.scriptText:
// to fetch the customerid and store it in custom properties.
import com.eviware.soapui.support.XmlHolder
def resp= context.testCase.testSteps['Create Customer without Address'].getPropertyValue('Response')

log.info resp
def holder = new XmlHolder(resp)
def customerId= holder.getNodeValue("//*:id")
context.testCase.setPropertyValue("customerId",customerId)

con:config.script:
//if address is present in create customer goto search by addressID else restireveCustomer details by customerID
def defaultBilling = context.expand('${Create Customer without Address#Response#//*:defaultBilling}')

log.info "$defaultBilling"
def addressId =testRunner.testCase.getPropertyValue("addressId")
log.info "$addressId"
if("$defaultBilling"!="$addressId")
{
    log.info "Address is not available for customer"

    testRunner.gotoStepByName("Retrieve by CustomerId")
}
else
{
    log.info "addressId is $defaultBilling"
    testRunner.gotoStepByName("Retrieve by AddressId")
}

//if customer search is successful using addressId ,goto deleteCustomer
def id = context.expand('${Retrieve by AddressId#Response#//*:id}')

log.info "$id"
def addressId =testRunner.testCase.getPropertyValue("addressId")
log.info "$addressId"
if("$id"!="$addressId")
{
    log.info "Address is not available for customer"

    testRunner.gotoStepByName("Retrieve by CustomerId")
}
else
{
    log.info "addressId is $id"
    testRunner.gotoStepByName("Delete Customer")
}
